/*n表示输入整数的位数k表示欲删除的位数*/#include<iostream>#include<math.h>#include<cstdlib>using namespace std;int main(){        long in_num;        int n,k;        int i,tmp;        char choice;        bool flag=true;        while(flag){                cout<<"*** Please input an integer:";                cin>>in_num;                cout<<"*** Please input the number of digits to be deleted:";                cin>>k;                n=log10(in_num)+1;                if(k>=n) {                        cout<<"<<< k cannot be greater than n, Please retry >>>"<<endl;                        continue;                }                //将整数各位取至数组中                int a[n];                tmp=in_num;                for(i=n-1;i>=0;i--){                        a[i]=tmp%10;                        tmp/=10;                }                //控制删去的个数                for(i=1;i<=k;i++){                        for(int j=0;j<n-i;j++){                                if(a[j]<a[j+1]) continue;                                else{ a[j]=-1; break;}                        }                        //删掉的数的位置被赋值-1，现在把-1以后的数都往前挪                        for(int j=0;j<n-i;j++){                                if(a[j]==-1){                                        a[j]=a[j+1];                                        a[j+1]=-1;                                }                        }                }                cout<<"*** After deletion: ";                for(i=0;i<n-k;i++)                        cout<<a[i];                cout <<endl;                cout<<"*** Start over? (Y/N)";                cin>>choice;                cout<<endl;                if(choice=='Y'||choice=='y');                if(choice=='N'||choice =='n') flag=false;        }        system("pause");        return 0;}
